exports.add = (x) => (list) =>
	(list[list.length] = x, list);

exports.applyTo = (f) => (list) =>
	f.apply(undefined, list);

exports.exec = (list) =>
	list.forEach(f => f());

exports.fill = (f, count) => (list) =>
	count > 0
		? exports.fill(f, --count)(exports.add(f())(list))
		: list;

exports.fillNew = (f, len) =>
	exports.fill(f, len)([]);

exports.ifEmpty = (f) => (list) =>
	void (exports.isEmpty(list) && f());

exports.isEmpty = (list) =>
	list.length === 0;
